css:
.btn{
	padding:6px 12px;
	cursor:pointer;
	display:inline-block;
	color:#fff;
	border-radius:4px;
	transition:background .2s;
	margin:1px;
}
.btn:hover{
	background:#ccc;
	font-weight:bold;
}
.btn-success{
	background:#16c559;
}
.btn-danger{
	background:#cb1515;
}
.btn-warning{
	background:#d9811c;
}
.btn-info{
	background:#1bcdd0;
}
.btn-default{
	background:#a5a1a1;
}
