css:
.menu{
	display:flex !important;
	user-select:none;
}
.menu-item{
	display:inline-block;
	position:relative;
	z-index:1;
}
.menu-item:hover{
	background:#fff;
	color:#000;
}
.has-child ul label{
	display:block;
	min-width:100px;
}
.childs-abs > ul, .childs-abs > .has-child > ul{
	top:100% !important;
	left:0 !important;
	text-align:left;
}
.menu-item label{
	display:flex;
	flex-direction:column;
	margin:1px;
	padding:4px 8px;
	cursor:pointer;
}
.menu > .has-child > ul{
	padding:6px;
	left:0px;
	top:100%;
}
.childs-abs ul{
	position:absolute;
}
.has-child > ul{
	background:#fff;
	color:#000;
	box-shadow:rgba(0, 0, 0, 0.24) 0px 3px 8px;
	display:none;
	top:0px;
	left:100%;
	padding:0;
	margin:0;
	transition:all .5s;
}
.has-child:hover > ul{
	display:block;
}
.has-child > label{
	padding-right:15px !important;
}
.menu-item .has-child > label:after{
	content:'›';
	position:absolute;
	right:3px;
}
.menu-item .has-child:hover > label:after{
	content:'‹';
}
.has-child ul label:hover{
	background:#ccc;
	color:#fff;
}
