css:
.flex {
    display: flex;
}
.flex-c {
    flex-direction: column;
}
.h {
    height: 100%;
}
.jcenter {
    justify-content: center;
    align-content: center;
}
.tab {
    display: flex;
    flex-direction: column;
}
.tab-header {
    background: #44ecf8;
    padding: 10px 0px 0px 10px;
    min-height: 40px;
}
.tab-body {
    padding: .5em;
}
.tab-body input {
    display: none;
}
.tab-body input:checked ~ .tab-item-container {
    display: block;
}
.tab-item-container {
    display: none;
    width: 100%;
    height: 100%;
}
.active {
    background: #ffffff;
}
.tab-header-item {
    cursor: pointer;
    padding: 0px 12px;
}
